<?php
 namespace App\Http\Controllers; use App\Models\accounts; use App\Models\expenses; use App\Models\products; use App\Models\purchase_details; use App\Models\sale_details; use App\Models\sales; use Carbon\Carbon; use Illuminate\Container\Attributes\DB; use Illuminate\Http\Request; class dashboardController extends Controller { public function index() { $months = array(); for ($i = 0; $i < 12; $i++) { $date = Carbon::now()->subMonths($i); $firstDay = $date->copy()->firstOfMonth()->toDateString(); $lastDay = $date->copy()->lastOfMonth()->toDateString(); $monthName = $date->format("\x4d"); $months[] = array("\146\151\162\163\x74" => $firstDay, "\154\141\x73\164" => $lastDay, "\x6e\x61\x6d\x65" => $monthName); } $months = array_reverse($months); $sales = array(); $monthNames = array(); $expenses = array(); $products = products::all(); $profits = array(); $last_sale = 0; $last_expense = 0; $last_profit = 0; foreach ($months as $key => $month) { $first = $month["\146\151\162\163\x74"]; $last = $month["\x6c\x61\x73\164"]; $sale = sales::whereBetween("\144\x61\x74\145", array($first, $last))->count(); $expense = expenses::whereBetween("\x64\x61\x74\x65", array($first, $last))->sum("\x61\155\157\165\156\x74"); $sales[] = $sale; $expenses[] = $expense; $monthNames[] = $month["\156\141\155\x65"]; $profit = 0; foreach ($products as $product) { $purchase_price = avgPurchasePrice($first, $last, $product->id); $sale_price = avgSalePrice($first, $last, $product->id); $sold = sale_details::where("\160\x72\x6f\144\x75\143\x74\111\x44", $product->id)->whereBetween("\144\x61\x74\145", array($first, $last))->sum("\x71\164\x79"); $ppi = $sale_price - $purchase_price; $ppp = $ppi * $sold; $profit += $ppp; } $profits[] = $profit - $expense; $last_sale = $sale; $last_expense = $expense; $last_profit = $profit; } $topProducts = products::withSum("\163\141\x6c\x65\x44\145\x74\x61\x69\154\163", "\161\164\171")->withSum("\163\141\x6c\x65\x44\145\x74\141\x69\x6c\x73", "\141\x6d\157\x75\156\164")->orderByDesc("\163\141\x6c\x65\x5f\144\x65\164\141\151\154\163\137\163\x75\155\x5f\161\x74\171")->take(5)->get(); $topProductsArray = array(); foreach ($topProducts as $product) { $stock = getStock($product->id); $price = avgSalePrice("\141\154\154", "\141\x6c\x6c", $product->id); $topProductsArray[] = array("\x6e\x61\155\145" => $product->name, "\160\x72\x69\x63\145" => $price, "\x73\x74\x6f\143\x6b" => $stock, "\x61\x6d\x6f\165\x6e\164" => $product->sale_details_sum_amount, "\x73\x6f\154\144" => $product->sale_details_sum_qty); } $topCustomers = accounts::where("\x74\171\x70\145", "\103\x75\x73\164\157\155\x65\x72")->withSum("\163\x61\x6c\145", "\164\x6f\164\141\154")->orderByDesc("\163\x61\x6c\145\137\163\165\x6d\x5f\164\157\x74\x61\154")->take(5)->get(); $topCustomersArray = array(); foreach ($topCustomers as $customer) { if ($customer->id != 2) { $balance = getAccountBalance($customer->id); $customer_purchases = $customer->sale_sum_total; $topCustomersArray[] = array("\x6e\x61\155\x65" => $customer->title, "\160\x75\162\143\x68\x61\163\145\163" => $customer_purchases, "\142\x61\x6c\x61\156\143\x65" => $balance); } } return view("\x64\141\x73\x68\142\x6f\141\x72\x64\x2e\151\x6e\x64\145\x78", compact("\x73\x61\x6c\145\163", "\155\157\x6e\x74\150\x4e\x61\x6d\x65\163", "\145\170\x70\145\156\163\145\163", "\160\162\157\146\151\164\x73", "\154\x61\163\164\x5f\163\x61\x6c\x65", "\154\141\163\164\137\145\170\160\145\x6e\x73\x65", "\x6c\141\x73\164\137\160\x72\x6f\x66\x69\164", "\x74\x6f\160\120\x72\157\144\165\x63\164\163\101\162\162\x61\171", "\x74\x6f\x70\x43\165\x73\164\x6f\155\145\162\x73\x41\162\162\x61\x79")); } }